$(document).ready(function(){
	var usersend = $('#usersend').val();
	var userrever = $('#userrever').val();

	var loadajax = function(){
		$.ajax({
			method: 'get',
			url: 'scriptphp/loadmessage.php?usersend='+usersend+'&&userrever='+userrever,
			success:function(data){
				$('#messageloadjs').html(data);
			}
		});
	}
	setInterval(loadajax,500);


	//$('#messageloadjs').load('scriptphp/loadmessage.php?usersend='+usersend+'&&userrever='+userrever);

	$("#submitinserteuser").click(function(){
		var massagesendjs = $('#massagesendjs').val().trim();
		if (massagesendjs && usersend && userrever) {
			$.ajax({
				url:"scriptphp/sendmassage.php",
				method:"POST",
				data:$('#formmessagesub').serialize(),
				success:function(data){
					$('#massagesendjs').val("");
				}
			});
		}
		return false;
	});
});